      _push(`<div${ssrRenderAttrs(mergeProps({ class: "flex justify-center pb-[50px] bg-[url('@/assets/img/bg.jpg')] bg-contain" }, _attrs))} data-v-6b8c245f><div class="w-[55%] mt-[80px] pt-[30px] relative" data-v-6b8c245f><div class="fixed right-[19%] translate-x-full" data-v-6b8c245f>`);
